// import Typography from '@material-ui/core/Typography';
import Slider from '@material-ui/core/Slider';
import './doubleSlider.css';
import { useState, useRef } from 'react';
import * as tf from '@tensorflow/tfjs';

let decoder;
let result;
const url = {
  modelLink: 'https://storage.googleapis.com/test-tfjs/model.json',
  binData: 'https://storage.googleapis.com/test-tfjs/group1-shard1of1.bin'
};

function DoubleSlider() {  
    const [sliderVState, setSliderVState] = useState(0);
    const [sliderHState, setSliderHState] = useState(0);
    const canvasRef = useRef();
    let handleVChange = (e) => {
      console.log(e, val);
      setSliderVState(val);
    }
    let handleHChange = (e, val) => {
      setSliderHState(val);
    }
    async function loadModel() {
      try {
        decoder = await tf.loadLayersModel(url.modelLink);
      } catch (err) {
        alert("model loading error....");
      }
    }
    async function processState() {
      await loadModel();
      let inputTensor = tf.tensor([sliderHState, sliderVState],[1, 2]);
      result = decoder.predict(inputTensor,{batchSize: 1}).reshape([28,28]);
      await tf.browser.toPixels(result, canvasRef.current);
    }

    processState();
    // console.log(sliderHState,sliderVState);
    return (
    <div className="large-box">  
      <div className="image-box">
        <canvas ref={canvasRef}></canvas>
      </div>
      <div className="vSlider">
        <Slider
          orientation="vertical"
          // getAriaValueText={valuetext}
          defaultValue={.01}
          aria-labelledby="discrete-slider-small-steps"
          min={0}
          max={1}
          marks
          valueLabelDisplay="auto"
          onChange={handleVChange}
        />
      </div>
      <div className="hSlider">
        <Slider
          defaultValue={0}
          // getAriaValueText={valuetext}
          aria-labelledby="discrete-slider-small-steps"
          step={.01}
          marks
          min={0}
          max={1}
          valueLabelDisplay="auto"
          onChange={handleHChange}
        />
      </div>
    </div>
    );
  }
  export default DoubleSlider;
